test('normalizes src', () => {
  const expected = '^/about$';
  const sources = [{
    src: '/about'
  }, {
    src: '/about$'
  }, {
    src: '\\/about'
  }, {
    src: '\\/about$'
  }, {
    src: '^/about'
  }, {
    src: '^/about$'
  }, {
    src: '^\\/about'
  }, {
    src: '^\\/about$'
  }];
  assertValid(sources);
  const normalized = normalizeRoutes(sources);
  assert.equal(normalized.error, null);
  assert.notEqual(normalized.routes, null);
  if (normalized.routes) {
    normalized.routes.forEach(route => {
      if (isHandler(route)) {
        assert.fail(`Normalizer returned: { handle: ${route.handle} } instead of { src: ${expected} }`);
      } else {
        assert.strictEqual(route.src, expected);
      }
    });
  }
});

