test('mergeRoutes ensure `handle: error` comes last', () => {
  const userRoutes = [];
  const builds = [{
    use: '@vercel/static-build',
    entrypoint: 'packge.json',
    routes: [{
      src: '^/home$',
      status: 301,
      headers: {
        Location: '/'
      }
    }]
  }, {
    use: '@vercel/zero-config-routes',
    entrypoint: '/',
    routes: [{
      handle: 'error'
    }, {
      status: 404,
      src: '^/(?!.*api).*$',
      dest: '404.html'
    }]
  }];
  const actual = mergeRoutes({
    userRoutes,
    builds
  });
  const expected = [{
    status: 301,
    src: '^/home$',
    headers: {
      Location: '/'
    }
  }, {
    handle: 'error'
  }, {
    status: 404,
    src: '^/(?!.*api).*$',
    dest: '404.html'
  }];
  deepStrictEqual(actual, expected);
});

