it('calls changeType if needed and stores current track info', function () {
  const getSourceBufferTypes = sandbox.stub(bufferController, 'getSourceBufferTypes').returns(['audio', 'video']);
  /* eslint-disable-next-line no-unused-vars */

  const appendChangeType = sandbox.stub(bufferController, 'appendChangeType');
  const buffer = {
    changeType: sandbox.stub()
  };
  const originalAudioTrack = {
    id: 'main',
    codec: 'mp4a.40.2',
    levelCodec: undefined,
    container: 'audio/mp4',
    metadata: {
      channelCount: 1
    }
  };
  const newAudioTrack = {
    id: 'main',
    codec: 'mp4a.40.5',
    levelCodec: undefined,
    container: 'audio/mp4',
    metadata: {
      channelCount: 1
    }
  };
  bufferController.tracks = {
    audio: { ...originalAudioTrack,
      buffer
    }
  };
  bufferController.onBufferCodecs(Events.BUFFER_CODECS, {
    audio: newAudioTrack
  });
  expect(getSourceBufferTypes).to.have.been.calledOnce;
  expect(bufferController.appendChangeType).to.have.been.calledOnce;
  expect(bufferController.appendChangeType).to.have.been.calledWith('audio', 'audio/mp4;codecs=mp4a.40.5');
  expect(bufferController.tracks.audio).to.deep.equal({
    buffer,
    ...newAudioTrack
  });
  bufferController.onBufferCodecs(Events.BUFFER_CODECS, {
    audio: originalAudioTrack
  });
  expect(getSourceBufferTypes).to.have.been.calledTwice;
  expect(bufferController.appendChangeType).to.have.been.calledTwice;
  expect(bufferController.appendChangeType).to.have.been.calledWith('audio', 'audio/mp4;codecs=mp4a.40.2');
  expect(bufferController.tracks.audio).to.deep.equal({
    buffer,
    ...originalAudioTrack
  });
});

