it('should trigger key system error(s) when bad encrypted data is received', function (done) {
  const reqMediaKsAccessSpy = sinon.spy(function () {
    return Promise.resolve({// Media-keys mock
    });
  });
  setupEach({
    emeEnabled: true,
    requestMediaKeySystemAccessFunc: reqMediaKsAccessSpy
  });
  const badData = {
    initDataType: 'cenc',
    initData: 'bad data'
  };
  emeController.onMediaAttached(Events.MEDIA_ATTACHED, {
    media
  });
  emeController.onManifestParsed(Events.MANIFEST_PARSED, {
    levels: fakeLevels
  });
  media.emit('encrypted', badData);
  self.setTimeout(function () {
    expect(emeController.hls.trigger).to.have.been.calledTwice;
    expect(emeController.hls.trigger.args[0][1].details).to.equal(ErrorDetails.KEY_SYSTEM_NO_KEYS);
    expect(emeController.hls.trigger.args[1][1].details).to.equal(ErrorDetails.KEY_SYSTEM_NO_SESSION);
    done();
  }, 0);
});

it('should close all media key sessions and remove media keys when media is detached', function (done) {
  const reqMediaKsAccessSpy = sinon.spy(function () {
    return Promise.resolve({// Media-keys mock
    });
  });
  const keySessionCloseSpy = sinon.spy(() => Promise.resolve());
  setupEach({
    emeEnabled: true,
    requestMediaKeySystemAccessFunc: reqMediaKsAccessSpy
  });
  emeController.onMediaAttached(Events.MEDIA_ATTACHED, {
    media
  });
  emeController._mediaKeysList = [{
    mediaKeysSession: {
      close: keySessionCloseSpy
    }
  }];
  emeController.onMediaDetached(Events.MEDIA_DETACHED);
  self.setTimeout(function () {
    expect(keySessionCloseSpy.callCount).to.equal(1);
    expect(emeController._mediaKeysList.length).to.equal(0);
    expect(media.setMediaKeys.calledWith(null)).to.be.true;
    done();
  }, 0);
});

it('returns default estimate if bw estimator not available yet', function () {
  const defaultEstimate = 5e5;
  const bwEstimator = new EwmaBandWidthEstimator(0, 0, defaultEstimate);
  expect(bwEstimator.getEstimate()).to.equal(5e5);
});

it('returns last bitrate is fast=slow=0', function () {
  const defaultEstimate = 5e5;
  const bwEstimator = new EwmaBandWidthEstimator(0, 0, defaultEstimate);
  bwEstimator.sample(8000, 1000000);
  expect(bwEstimator.getEstimate()).to.equal(1000000);
  bwEstimator.sample(4000, 1000000);
  expect(bwEstimator.getEstimate()).to.equal(2000000);
  bwEstimator.sample(1000, 1000000);
  expect(bwEstimator.getEstimate()).to.equal(8000000);
});

it('returns correct value bitrate is slow=15,fast=4', function () {
  const defaultEstimate = 5e5;
  const bwEstimator = new EwmaBandWidthEstimator(15, 4, defaultEstimate);
  bwEstimator.sample(8000, 1000000);
  expect(bwEstimator.getEstimate()).to.equal(1000000);
  bwEstimator.sample(4000, 1000000);
  expect(bwEstimator.getEstimate()).to.closeTo(1396480.1544736226, 0.000000001);
  bwEstimator.sample(1000, 1000000);
  expect(bwEstimator.getEstimate()).to.closeTo(2056826.9489827948, 0.000000001);
});

it('returns correct value bitrate is slow=9,fast=5', function () {
  const defaultEstimate = 5e5;
  const bwEstimator = new EwmaBandWidthEstimator(9, 5, defaultEstimate);
  bwEstimator.sample(8000, 1000000);
  expect(bwEstimator.getEstimate()).to.equal(1000000);
  bwEstimator.sample(4000, 1000000);
  expect(bwEstimator.getEstimate()).to.closeTo(1439580.319105247, 0.000000001);
  bwEstimator.sample(1000, 1000000);
  expect(bwEstimator.getEstimate()).to.closeTo(2208342.324322311, 0.000000001);
});

it('returns correct value after updating slow and fast', function () {
  const defaultEstimate = 5e5;
  const bwEstimator = new EwmaBandWidthEstimator(9, 3, defaultEstimate);
  expect(bwEstimator.getEstimate()).to.equal(defaultEstimate);
  bwEstimator.sample(8000, 1000000);
  expect(bwEstimator.getEstimate()).to.equal(1000000);
  bwEstimator.sample(4000, 1000000);
  expect(bwEstimator.getEstimate()).to.closeTo(1439580.319105247, 0.000000001);
  bwEstimator.update(15, 4);
  expect(bwEstimator.getEstimate()).to.closeTo(1878125.393685882, 0.000000001);
  bwEstimator.sample(1000, 1000000);
  expect(bwEstimator.getEstimate()).to.closeTo(2966543.443461984, 0.000000001);
});

it('returns correct value when updating before a sample', function () {
  const defaultEstimate = 5e5;
  const bwEstimator = new EwmaBandWidthEstimator(9, 3, defaultEstimate);
  bwEstimator.update(15, 4);
  expect(bwEstimator.getEstimate()).to.equal(defaultEstimate);
  bwEstimator.sample(8000, 1000000);
  expect(bwEstimator.getEstimate()).to.equal(1000000);
});

it('finds a fragment with SN sequential to the previous fragment', function () {
  const actual = findFragmentByPTS(fragPrevious, mockFragments, bufferEnd, tolerance);
  const resultSN = actual ? actual.sn : -1;
  expect(actual).to.equal(mockFragments[3], 'Expected sn 3, found sn segment ' + resultSN);
  expect(binarySearchSpy).to.have.not.been.called;
});

it('chooses the fragment with the next SN if its contiguous with the end of the buffer', function () {
  const actual = findFragmentByPTS(mockFragments[0], mockFragments, mockFragments[0].duration, tolerance);
  expect(actual).to.equal(mockFragments[1], `expected sn ${mockFragments[1].sn}, but got sn ${actual ? actual.sn : null}`);
  expect(binarySearchSpy).to.have.not.been.called;
});

it('chooses the fragment with the next SN if its contiguous with the end of previous fragment', function () {
  // See https://github.com/video-dev/hls.js/issues/2776
  const bufferEnd = 60.139636;
  const fragments = [{
    deltaPTS: 0.012346258503441732,
    cc: 2,
    duration: 5.017346258503444,
    start: 55.21705215419478,
    sn: 11,
    level: 0
  }, {
    deltaPTS: 0,
    cc: 2,
    duration: 0.033,
    start: 60.234398412698226,
    sn: 12,
    level: 0
  }];
  const fragPrevious = fragments[0];
  const actual = findFragmentByPTS(fragPrevious, fragments, bufferEnd, tolerance);
  expect(actual).to.equal(fragments[1], `expected sn ${fragments[1].sn}, but got sn ${actual ? actual.sn : null}`);
});

it('uses BinarySearch to find a fragment if the subsequent one is not within tolerance', function () {
  const fragments = [mockFragments[0], mockFragments[mockFragments.length - 1]];
  findFragmentByPTS(fragments[0], fragments, bufferEnd, tolerance);
  expect(binarySearchSpy).to.have.been.calledOnce;
});

it('returns null when there is a gap in sn and start-end time', function () {
  const bufferEnd = 49;
  const fragments = [{
    deltaPTS: 0,
    cc: 0,
    duration: 5,
    start: 54,
    sn: 5,
    level: 0
  }, {
    deltaPTS: 0,
    cc: 0,
    duration: 5,
    start: 59,
    sn: 5,
    level: 0
  }, {
    deltaPTS: 0,
    cc: 0,
    duration: 5,
    start: 64,
    sn: 5,
    level: 0
  }]; // sn is not contiguous, and there is a gap between start and end

  const fragPrevious = {
    deltaPTS: 0,
    cc: 0,
    duration: 5,
    start: 44,
    sn: 1,
    level: 0
  };
  const actual = findFragmentByPTS(fragPrevious, fragments, bufferEnd, tolerance);
  expect(actual).to.equal(null);
});

