it('should return 29 (0b 1110 1)', function () {
  reader.skipBits(5);
  expect(reader.readBits(5)).to.equal(0b11101);
});

it('should return 7099 (0b 1101 1101 1101 1)', function () {
  reader.skipBits(6 + 56 + 4);
  expect(reader.readBits(13)).to.equal(0b1101110111011);
});

it('should return UINT_MAX(4294967295) twice', function () {
  reader.skipBits(39);
  expect(reader.readBits(32)).to.equal(4294967295); //0b 11111111 11111111 11111111 11111111
  expect(reader.readBits(32)).to.equal(4294967295); //0b 11111111 11111111 11111111 11111111
});

it('should throw error if can no longer buffer be skip', function () {
  expect(reader.readBits(4)).to.equal(0b1010);
  expect(function () {
    reader.skipBits(16);
  }).to.throw('no bytes available');
});

it('should throw error if can no longer buffer be read', function () {
  expect(function () {
    reader.readBits(16);
  }).to.throw('no bits available');
});

it('handles new cue without dispatching', function () {
  outputFilter.newCue(0, 1, {});
  expect(timelineController.addCues).to.not.have.been.called;
  expect(timelineController.createCaptionsTrack).to.have.been.called;
});

it('handles single cue and dispatch', function () {
  const lastScreen = {};
  outputFilter.newCue(0, 1, lastScreen);
  outputFilter.dispatchCue();
  expect(timelineController.addCues).to.have.been.calledOnce;
  expect(timelineController.addCues).to.have.been.calledWith(1, 0, 1, lastScreen);
});

it('handles multiple cues and dispatch', function () {
  outputFilter.newCue(0, 1, {});
  outputFilter.newCue(1, 2, {});
  const lastScreen = {};
  outputFilter.newCue(3, 4, lastScreen);
  outputFilter.dispatchCue();
  expect(timelineController.addCues).to.have.been.calledOnce;
  expect(timelineController.addCues).to.have.been.calledWith(1, 0, 4, lastScreen);
});

it('does not dispatch empty cues', function () {
  outputFilter.newCue(0, 1, {});
  expect(timelineController.addCues).to.not.have.been.called;
  outputFilter.dispatchCue();
  expect(timelineController.addCues).to.have.been.calledOnce;
  outputFilter.dispatchCue();
  expect(timelineController.addCues).to.have.been.calledOnce;
});

it('should have the provided track as data', function (done) {
  const dispatchSpy = sinon.spy(video, 'dispatchEvent');
  video.addEventListener('addtrack', function (e) {
    expect(e.track).to.equal(track);
    done();
  });
  sendAddTrackEvent(track, video);
  expect(dispatchSpy.calledOnce).to.be.true;
});

it('should fallback to document.createEvent if window.Event constructor throws', function (done) {
  const stub = sinon.stub(self, 'Event');
  stub.throws();
  const spy = sinon.spy(document, 'createEvent');
  video.addEventListener('addtrack', function (e) {
    expect(e.track).to.equal(track);
    done();
  });
  sendAddTrackEvent(track, video);
  expect(spy.calledOnce).to.be.true;
});

it('should not fail with empty cue list', function () {
  const emptyTrack = video.addTextTrack('subtitles', 'empty');
  expect(clearCurrentCues(emptyTrack)).to.not.throw;
});

it('should clear the cues from track', function () {
  clearCurrentCues(track);
  expect(track.cues.length).to.equal(0);
});
