    compile(`<script setup>
    let bar = 1
    defineProps({
      foo: {
        default: () => bar
      }
    })
    </script>`)
  ).toThrow(`cannot reference locally declared variables`)

  expect(() =>
    compile(`<script setup>
    let bar = 'hello'
