it('should observe mutations', () => {
  let dummy
  const map = reactive(new Map())
  effect(() => {
    dummy = map.get('key')
  })

  expect(dummy).toBe(undefined)
  map.set('key', 'value')
  expect(dummy).toBe('value')
  map.set('key', 'value2')
  expect(dummy).toBe('value2')
  map.delete('key')
  expect(dummy).toBe(undefined)
})

it('should observe mutations with observed value as key', () => {
  let dummy
  const key = reactive({})
  const value = reactive({})
  const map = reactive(new Map())
  effect(() => {
    dummy = map.get(key)
  })

  expect(dummy).toBe(undefined)
  map.set(key, value)
  expect(dummy).toBe(value)
  map.delete(key)
  expect(dummy).toBe(undefined)
})

it('should observe for of iteration', () => {
  let dummy
  const map = reactive(new Map())
  effect(() => {
    dummy = 0
    // eslint-disable-next-line no-unused-vars
    for (let [key, num] of map) {
      key
      dummy += num
    }
  })

  expect(dummy).toBe(0)
  map.set('key1', 3)
  expect(dummy).toBe(3)
  map.set('key2', 2)
  expect(dummy).toBe(5)
  // iteration should track mutation of existing entries (#709)
  map.set('key1', 4)
  expect(dummy).toBe(6)
  map.delete('key1')
  expect(dummy).toBe(2)
  map.clear()
  expect(dummy).toBe(0)
})

it('should observe forEach iteration', () => {
  let dummy: any
  const map = reactive(new Map())
  effect(() => {
    dummy = 0
    map.forEach((num: any) => (dummy += num))
  })

  expect(dummy).toBe(0)
  map.set('key1', 3)
  expect(dummy).toBe(3)
  map.set('key2', 2)
  expect(dummy).toBe(5)
  // iteration should track mutation of existing entries (#709)
  map.set('key1', 4)
  expect(dummy).toBe(6)
  map.delete('key1')
  expect(dummy).toBe(2)
  map.clear()
  expect(dummy).toBe(0)
})

it('should observe keys iteration', () => {
  let dummy
  const map = reactive(new Map())
