it('debug: onTrack', () => {
  let events: DebuggerEvent[] = []
  const onTrack = vi.fn((e: DebuggerEvent) => {
    events.push(e)
  })
  const obj = reactive({ foo: 1, bar: 2 })
  const c = computed(() => (obj.foo, 'bar' in obj, Object.keys(obj)), {
    onTrack
  })
  expect(c.value).toEqual(['foo', 'bar'])
  expect(onTrack).toHaveBeenCalledTimes(3)
  expect(events).toEqual([
    {
      effect: c.effect,
      target: toRaw(obj),
      type: TrackOpTypes.GET,
      key: 'foo'
    },
    {
      effect: c.effect,
      target: toRaw(obj),
      type: TrackOpTypes.HAS,
      key: 'bar'
    },
    {
      effect: c.effect,
      target: toRaw(obj),
      type: TrackOpTypes.ITERATE,
      key: ITERATE_KEY
    }
  ])
})

it('debug: onTrigger', () => {
  let events: DebuggerEvent[] = []
  const onTrigger = vi.fn((e: DebuggerEvent) => {
    events.push(e)
  })
  const obj = reactive({ foo: 1 })
  const c = computed(() => obj.foo, { onTrigger })

  // computed won't trigger compute until accessed
  c.value

  obj.foo++
  expect(c.value).toBe(2)
  expect(onTrigger).toHaveBeenCalledTimes(1)
  expect(events[0]).toEqual({
    effect: c.effect,
    target: toRaw(obj),
    type: TriggerOpTypes.SET,
    key: 'foo',
    oldValue: 1,
    newValue: 2
  })

  // @ts-ignore
  delete obj.foo
  expect(c.value).toBeUndefined()
  expect(onTrigger).toHaveBeenCalledTimes(2)
  expect(events[1]).toEqual({
    effect: c.effect,
    target: toRaw(obj),
    type: TriggerOpTypes.DELETE,
    key: 'foo',
    oldValue: 2
  })
})

test('should only trigger once on multiple mutations', async () => {
  const src = ref(0)
  const c = deferredComputed(() => src.value)
  const spy = vi.fn()
  effect(() => {
    spy(c.value)
  })
  expect(spy).toHaveBeenCalledTimes(1)
  src.value = 1
  src.value = 2
  src.value = 3
  // not called yet
  expect(spy).toHaveBeenCalledTimes(1)
  await tick
  // should only trigger once
  expect(spy).toHaveBeenCalledTimes(2)
  expect(spy).toHaveBeenCalledWith(c.value)
})

test('should not trigger if value did not change', async () => {
  const src = ref(0)
  const c = deferredComputed(() => src.value % 2)
  const spy = vi.fn()
  effect(() => {
    spy(c.value)
  })
  expect(spy).toHaveBeenCalledTimes(1)
  src.value = 1
  src.value = 2

  await tick
  // should not trigger
  expect(spy).toHaveBeenCalledTimes(1)

  src.value = 3
  src.value = 4
  src.value = 5
  await tick
  // should trigger because latest value changes
  expect(spy).toHaveBeenCalledTimes(2)
})

test('chained computed trigger', async () => {
  const effectSpy = vi.fn()
  const c1Spy = vi.fn()
  const c2Spy = vi.fn()

  const src = ref(0)
  const c1 = deferredComputed(() => {
    c1Spy()
    return src.value % 2
  })
  const c2 = computed(() => {
    c2Spy()
    return c1.value + 1
  })

  effect(() => {
    effectSpy(c2.value)
  })

  expect(c1Spy).toHaveBeenCalledTimes(1)
  expect(c2Spy).toHaveBeenCalledTimes(1)
  expect(effectSpy).toHaveBeenCalledTimes(1)

  src.value = 1
  await tick
  expect(c1Spy).toHaveBeenCalledTimes(2)
  expect(c2Spy).toHaveBeenCalledTimes(2)
  expect(effectSpy).toHaveBeenCalledTimes(2)
})

test('chained computed avoid re-compute', async () => {
  const effectSpy = vi.fn()
  const c1Spy = vi.fn()
  const c2Spy = vi.fn()

  const src = ref(0)
  const c1 = deferredComputed(() => {
    c1Spy()
    return src.value % 2
  })
  const c2 = computed(() => {
    c2Spy()
    return c1.value + 1
  })

  effect(() => {
    effectSpy(c2.value)
  })

  expect(effectSpy).toHaveBeenCalledTimes(1)
  src.value = 2
  src.value = 4
  src.value = 6
  await tick
  // c1 should re-compute once.
  expect(c1Spy).toHaveBeenCalledTimes(2)
  // c2 should not have to re-compute because c1 did not change.
  expect(c2Spy).toHaveBeenCalledTimes(1)
  // effect should not trigger because c2 did not change.
  expect(effectSpy).toHaveBeenCalledTimes(1)
})

test('chained computed value invalidation', async () => {
  const effectSpy = vi.fn()
  const c1Spy = vi.fn()
  const c2Spy = vi.fn()

  const src = ref(0)
  const c1 = deferredComputed(() => {
    c1Spy()
    return src.value % 2
  })
  const c2 = deferredComputed(() => {
    c2Spy()
    return c1.value + 1
  })

  effect(() => {
    effectSpy(c2.value)
  })

  expect(effectSpy).toHaveBeenCalledTimes(1)
  expect(effectSpy).toHaveBeenCalledWith(1)
  expect(c2.value).toBe(1)

  expect(c1Spy).toHaveBeenCalledTimes(1)
  expect(c2Spy).toHaveBeenCalledTimes(1)

  src.value = 1
  // value should be available sync
  expect(c2.value).toBe(2)
  expect(c2Spy).toHaveBeenCalledTimes(2)
})

