  expect(isRef(x)).toBe(true)
  expect(x.value).toBe(1)

  // source -> proxy
  a.x = 2
  expect(x.value).toBe(2)

  // proxy -> source
  x.value = 3
  expect(a.x).toBe(3)

  // reactivity
  let dummyX
  effect(() => {
    dummyX = x.value
  })
  expect(dummyX).toBe(x.value)

  // mutating source should trigger effect using the proxy refs
  a.x = 4
  expect(dummyX).toBe(4)

  // should keep ref
  const r = { x: ref(1) }
  expect(toRef(r, 'x')).toBe(r.x)
})

