test('retry (fail w/ max retry attempts)', async () => {
  let loaderCallCount = 0
  let reject: (e: Error) => void

  const Foo = defineAsyncComponent({
    loader: () => {
      loaderCallCount++
      return new Promise((_resolve, _reject) => {
        reject = _reject
      })
    },
    onError(error, retry, fail, attempts) {
