test('mixin', () => {
  const calls: string[] = []
  const mixinA = {
    data() {
      return {
        a: 1
      }
    },
    created(this: any) {
      calls.push('mixinA created')
      expect(this.a).toBe(1)
      expect(this.b).toBe(2)
      expect(this.c).toBe(3)
    },
    mounted() {
      calls.push('mixinA mounted')
    }
  }
  const mixinB = {
    name: 'mixinB',
    data() {
      return {
        b: 2
      }
    },
    created(this: any) {
      calls.push('mixinB created')
      expect(this.a).toBe(1)
      expect(this.b).toBe(2)
      expect(this.c).toBe(3)
    },
    mounted() {
      calls.push('mixinB mounted')
    }
  }
  const Comp = {
    data() {
      return {
        c: 3
      }
    },
    created(this: any) {
      calls.push('comp created')
      expect(this.a).toBe(1)
      expect(this.b).toBe(2)
      expect(this.c).toBe(3)
    },
    mounted() {
      calls.push('comp mounted')
    },
    render(this: any) {
      return `${this.a}${this.b}${this.c}`
    }
  }

  const app = createApp(Comp)
  app.mixin(mixinA)
  app.mixin(mixinB)

  app.mixin(mixinA)
  app.mixin(mixinB)
  expect(
    'Mixin has already been applied to target app'
  ).toHaveBeenWarnedTimes(2)
  expect(
    'Mixin has already been applied to target app: mixinB'
  ).toHaveBeenWarnedTimes(1)

  const root = nodeOps.createElement('div')
  app.mount(root)

  expect(serializeInner(root)).toBe(`123`)
  expect(calls).toEqual([
    'mixinA created',
    'mixinB created',
    'comp created',
    'mixinA mounted',
    'mixinB mounted',
    'comp mounted'
  ])
})

test('use', () => {
  const PluginA: Plugin = app => app.provide('foo', 1)
  const PluginB: Plugin = {
    install: (app, arg1, arg2) => app.provide('bar', arg1 + arg2)
  }
  class PluginC {
    someProperty = {}
    static install() {
      app.provide('baz', 2)
    }
  }
  const PluginD: any = undefined

  const Root = {
    setup() {
      const foo = inject('foo')
      const bar = inject('bar')
      return () => `${foo},${bar}`
    }
  }

  const app = createApp(Root)
  app.use(PluginA)
  app.use(PluginB, 1, 1)
  app.use(PluginC)

  const root = nodeOps.createElement('div')
  app.mount(root)
  expect(serializeInner(root)).toBe(`1,2`)

  app.use(PluginA)
  expect(
    `Plugin has already been applied to target app`
  ).toHaveBeenWarnedTimes(1)

  app.use(PluginD)
  expect(
    `A plugin must either be a function or an object with an "install" ` +
      `function.`
  ).toHaveBeenWarnedTimes(1)
})

