      return h('h1', 'Async component')
    }
  }
  let serverResolve: any
  let AsyncComp = defineAsyncComponent(
    () =>
      new Promise(r => {
        serverResolve = r
      })
  )

  const bol = ref(true)
  const App = {
    setup() {
      onMounted(() => {
        // change state, this makes updateComponent(AsyncComp) execute before
        // the async component is resolved
        bol.value = false
