it('should not fallthrough v-model listeners with corresponding declared prop', () => {
  let textFoo = ''
  let textBar = ''
  const click = vi.fn()

  const App = defineComponent({
    setup() {
      return () =>
        h(Child, {
          modelValue: textFoo,
          'onUpdate:modelValue': (val: string) => {
            textFoo = val
          }
        })
    }
  })

  const Child = defineComponent({
    props: ['modelValue'],
    setup(_props, { emit }) {
      return () =>
        h(GrandChild, {
          modelValue: textBar,
          'onUpdate:modelValue': (val: string) => {
            textBar = val
            emit('update:modelValue', 'from Child')
          }
        })
    }
  })

  const GrandChild = defineComponent({
    props: ['modelValue'],
    setup(_props, { emit }) {
      return () =>
        h('button', {
          onClick() {
            click()
            emit('update:modelValue', 'from GrandChild')
          }
        })
    }
  })

  const root = document.createElement('div')
  document.body.appendChild(root)
  render(h(App), root)

  const node = root.children[0] as HTMLElement

  node.dispatchEvent(new CustomEvent('click'))
  expect(click).toHaveBeenCalled()
  expect(textBar).toBe('from GrandChild')
  expect(textFoo).toBe('from Child')
})

