      return () => createVNode('div', null, show.value, PatchFlags.TEXT)
    }
  }

  const app = createApp({
    render() {
      return show.value
        ? (openBlock(),
          createBlock(
            Parent,
            { key: 0 },
            {
              default: withCtx(() => [createVNode(Child)]),
              _: SlotFlags.STABLE
            }
          ))
        : createCommentVNode('v-if', true)
    }
  })

  app.mount(root)
  expect(inner(root)).toBe('<div>true</div>')

  show.value = false
  await nextTick()
  expect(inner(root)).toBe('<!--v-if-->')
  expect(spyUnmounted).toHaveBeenCalledTimes(1)
})

test('fragments inside programmatically invoked compiled slot should de-opt properly', async () => {
  const Parent: FunctionalComponent = (_, { slots }) => slots.default!()
  const Dummy = () => 'dummy'

  const toggle = ref(true)
  const force = ref(0)

  const app = createApp({
    render() {
      if (!toggle.value) {
        return null
      }
      return h(
        Parent,
        { n: force.value },
        {
          default: withCtx(
            () => [
              createVNode('ul', null, [
                (openBlock(),
                createBlock(
                  Fragment,
                  null,
                  renderList(1, item => {
                    return createVNode('li', null, [createVNode(Dummy)])
                  }),
                  64 /* STABLE_FRAGMENT */
                ))
              ])
            ],
            undefined,
            true
          ),
          _: 1 /* STABLE */
        }
      )
    }
  })

  app.mount(root)

  // force a patch
  force.value++
  await nextTick()
  expect(inner(root)).toBe(`<ul><li>dummy</li></ul>`)

  // unmount
  toggle.value = false
  await nextTick()
  // should successfully unmount without error
  expect(inner(root)).toBe(`<!---->`)
})

