test('nextTick should capture scheduler flush errors', async () => {
  const err = new Error('test')
  queueJob(() => {
    throw err
  })
  try {
    await nextTick()
  } catch (e: any) {
    expect(e).toBe(err)
  }
  expect(
    `Unhandled error during execution of scheduler flush`
  ).toHaveBeenWarned()

  // this one should no longer error
  await nextTick()
})

test('should prevent self-triggering jobs by default', async () => {
  let count = 0
  const job = () => {
    if (count < 3) {
      count++
      queueJob(job)
    }
  }
  queueJob(job)
  await nextTick()
  // only runs once - a job cannot queue itself
  expect(count).toBe(1)
})

test('should allow explicitly marked jobs to trigger itself', async () => {
  // normal job
  let count = 0
  const job = () => {
    if (count < 3) {
      count++
      queueJob(job)
    }
  }
  job.allowRecurse = true
  queueJob(job)
  await nextTick()
  expect(count).toBe(3)

  // post cb
  const cb = () => {
    if (count < 5) {
      count++
      queuePostFlushCb(cb)
    }
  }
  cb.allowRecurse = true
  queuePostFlushCb(cb)
  await nextTick()
  expect(count).toBe(5)
})

test('flushPostFlushCbs', async () => {
  let count = 0

  const queueAndFlush = (hook: Function) => {
    queuePostFlushCb(hook)
    flushPostFlushCbs()
  }

  queueAndFlush(() => {
    queueAndFlush(() => {
      count++
    })
  })

  await nextTick()
  expect(count).toBe(1)
})

test('should not run stopped reactive effects', async () => {
  const spy = vi.fn()

  // simulate parent component that toggles child
  const job1 = () => {
    // @ts-ignore
    job2.active = false
  }
  // simulate child that's triggered by the same reactive change that
  // triggers its toggle
  const job2 = () => spy()
  expect(spy).toHaveBeenCalledTimes(0)

  queueJob(job1)
  queueJob(job2)
  await nextTick()

  // should not be called
  expect(spy).toHaveBeenCalledTimes(0)
})

it('flushPreFlushCbs inside a pre job', async () => {
  const spy = vi.fn()
  const job = () => {
    spy()
    flushPreFlushCbs()
  }
  job.pre = true
  queueJob(job)
  await nextTick()
  expect(spy).toHaveBeenCalledTimes(1)
})

