test('on suspense root', async () => {
  const state = reactive({ color: 'red' })
  const root = document.createElement('div')

  let resolveAsync: any
  let asyncPromise: any

  const AsyncComp = {
    setup() {
      asyncPromise = new Promise(r => {
        resolveAsync = () => {
          r(() => h('p', 'default'))
        }
      })
      return asyncPromise
    }
  }

  const App = {
    setup() {
      useCssVars(() => state)
      return () =>
        h(Suspense, null, {
          default: h(AsyncComp),
          fallback: h('div', 'fallback')
        })
    }
  }

  render(h(App), root)
  await nextTick()
  // css vars use with fallback tree
  for (const c of [].slice.call(root.children as any)) {
    expect((c as HTMLElement).style.getPropertyValue(`--color`)).toBe(`red`)
  }
  // AsyncComp resolve
  resolveAsync()
  await asyncPromise.then(() => {})
  // Suspense effects flush
  await nextTick()
  // css vars use with default tree
  for (const c of [].slice.call(root.children as any)) {
    expect((c as HTMLElement).style.getPropertyValue(`--color`)).toBe(`red`)
  }

  state.color = 'green'
  await nextTick()
  for (const c of [].slice.call(root.children as any)) {
    expect((c as HTMLElement).style.getPropertyValue(`--color`)).toBe('green')
  }
})

