it('should record ops', async () => {
  const state = reactive({
    id: 'test',
    text: 'hello'
  })

  const App = {
    render() {
      return h(
        'div',
        {
          id: state.id
        },
        state.text
      )
    }
  }
  const root = nodeOps.createElement('div')

  resetOps()
  render(h(App), root)
  const ops = dumpOps()

  expect(ops.length).toBe(4)

  expect(ops[0]).toEqual({
    type: NodeOpTypes.CREATE,
    nodeType: TestNodeTypes.ELEMENT,
    tag: 'div',
    targetNode: root.children[0]
  })

  expect(ops[1]).toEqual({
    type: NodeOpTypes.SET_ELEMENT_TEXT,
    text: 'hello',
    targetNode: root.children[0]
  })

  expect(ops[2]).toEqual({
    type: NodeOpTypes.PATCH,
    targetNode: root.children[0],
    propKey: 'id',
    propPrevValue: null,
    propNextValue: 'test'
  })

  expect(ops[3]).toEqual({
    type: NodeOpTypes.INSERT,
    targetNode: root.children[0],
    parentNode: root,
    refNode: null
  })

  // test update ops
  state.id = 'foo'
  state.text = 'bar'
  await nextTick()

  const updateOps = dumpOps()
  expect(updateOps.length).toBe(2)

  expect(updateOps[0]).toEqual({
    type: NodeOpTypes.SET_ELEMENT_TEXT,
    targetNode: root.children[0],
    text: 'bar'
  })

  expect(updateOps[1]).toEqual({
    type: NodeOpTypes.PATCH,
    targetNode: root.children[0],
    propKey: 'id',
    propPrevValue: 'test',
    propNextValue: 'foo'
  })
})

  it('should be able to serialize nodes', () => {
    const App = {
      render() {
        return h(
          'div',
          {
            id: 'test',
            boolean: ''
          },
          [h('span', 'foo'), 'hello']
        )
      }
    }
    const root = nodeOps.createElement('div')
    render(h(App), root)
    expect(serialize(root)).toEqual(
      `<div><div id="test" boolean><span>foo</span>hello</div></div>`
    )
    // indented output
    expect(serialize(root, 2)).toEqual(
      `<div>
  <div id="test" boolean>
    <span>
      foo
    </span>
    hello
  </div>
</div>`
    )
  })

