test('nested components with optimized slots', async () => {
  const Child = {
    props: ['msg'],
    ssrRender(ctx: any, push: any, parent: any) {
      push(`<div class="child">`)
      ssrRenderSlot(
        ctx.$slots,
        'default',
        { msg: 'from slot' },
        () => {
          push(`fallback`)
        },
        push,
        parent
      )
      push(`</div>`)
    }
  }

  expect(
    await render(
      createApp({
        ssrRender(_ctx, push, parent) {
          push(`<div>parent`)
          push(
            ssrRenderComponent(
              Child,
              { msg: 'hello' },
              {
                // optimized slot using string push
                default: (({ msg }, push, _p) => {
                  push(`<span>${msg}</span>`)
                }) as SSRSlot,
                // important to avoid slots being normalized
                _: 1 as any
              },
              parent
            )
          )
          push(`</div>`)
        }
      })
    )
  ).toBe(
    `<div>parent<div class="child">` +
      `<!--[--><span>from slot</span><!--]-->` +
      `</div></div>`
  )

  // test fallback
  expect(
    await render(
      createApp({
        ssrRender(_ctx, push, parent) {
          push(`<div>parent`)
          push(ssrRenderComponent(Child, { msg: 'hello' }, null, parent))
          push(`</div>`)
        }
      })
    )
  ).toBe(
    `<div>parent<div class="child"><!--[-->fallback<!--]--></div></div>`
  )
})

