      // <Wrapper><Child/></Wrapper>
      push(
        ssrRenderComponent(
          Wrapper,
          null,
          {
            default: withCtx(
              (_: any, push: any, parent: any, scopeId: string) => {
                push(ssrRenderComponent(Child, null, null, parent, scopeId))
              }
            ),
            _: 1
          } as any,
          parent
        )
      )
    }
  }

