      const One = {
        async setup() {
          return () => h('div', { class: 'test' }, 'one')
        }
      }
      const Two = {
        async setup() {
          return () => h('div', { class: 'test' }, 'two')
        }
      }
      createApp({
        template: `
          <div id="container">
            <transition mode="out-in">
              <Suspense>
                <component :is="view"/>
              </Suspense>
            </transition>
          </div>
          <button id="toggleBtn" @click="click">button</button>
        `,
        setup: () => {
          const view = shallowRef(One)
          const click = () => {
            view.value = view.value === One ? Two : One
          }
          return { view, click }
        }
      }).mount('#app')
    })

    await nextFrame()
    expect(await html('#container')).toBe(
      '<div class="test v-enter-active v-enter-to">one</div>'
    )
    await transitionFinish()
    expect(await html('#container')).toBe('<div class="test">one</div>')

    // leave
    await classWhenTransitionStart()
    await nextFrame()
    expect(await html('#container')).toBe(
      '<div class="test v-leave-active v-leave-to">one</div>'
    )
    await transitionFinish()
    await nextFrame()
    // expect(await html('#container')).toBe(
    //   '<div class="test v-enter-active v-enter-to">two</div>'
    // )
    await transitionFinish()
    expect(await html('#container')).toBe('<div class="test">two</div>')
  },
  E2E_TIMEOUT
)

test(
  'Suspense fallback should work with transition',
  async () => {
    await page().evaluate(() => {
      const { createApp, shallowRef, h } = (window as any).Vue

      const One = {
        template: `<div>{{ msg }}</div>`,
        setup() {
          return new Promise(_resolve => {
            // @ts-ignore
            window.resolve = () =>
              _resolve({
                msg: 'success'
              })
          })
        }
      }

      createApp({
        template: `
          <div id="container">
            <transition mode="out-in">
              <Suspense :timeout="0">
                <template #default>
                  <component :is="view" />
                </template>
                <template #fallback>
                  <div>Loading...</div>
                </template>
              </Suspense>
            </transition>
          </div>
          <button id="toggleBtn" @click="click">button</button>
        `,
        setup: () => {
          const view = shallowRef(null)
          const click = () => {
            view.value = view.value ? null : h(One)
          }
          return { view, click }
        }
      }).mount('#app')
    })

    expect(await html('#container')).toBe('<!---->')

    await click('#toggleBtn')
    await nextFrame()
    expect(await html('#container')).toBe('<div class="">Loading...</div>')

    await page().evaluate(() => {
      // @ts-ignore
      window.resolve()
    })

    await transitionFinish(duration * 2)
    expect(await html('#container')).toBe('<div class="">success</div>')
  },
  E2E_TIMEOUT
)

