      </div>
    </teleport>
    `,
    data() {
      return {
        count
      }
    }
  }
  createApp(App).mount(container)
  expect(container.innerHTML).toBe(`<!--teleport start--><!--teleport end-->`)
  expect(target.innerHTML).toBe(`<div><div>0</div></div>`)

  count.value++
  await nextTick()
  expect(container.innerHTML).toBe(`<!--teleport start--><!--teleport end-->`)
  expect(target.innerHTML).toBe(`<div><div>1</div></div>`)

  count.value++
