      return { list }
    },
    template: `<div>{{ list.length }}<div v-for="i in list" v-once>{{ i }}</div></div>`
  }
  const container = document.createElement('div')
  createApp(App).mount(container)

  expect(container.innerHTML).toBe(`<div>1<div>1</div></div>`)
  list.push(2)
  await nextTick()
  expect(container.innerHTML).toBe(`<div>2<div>1</div></div>`)
})

it('EMPTY_ARR should not change', () => {
  const App = {
    template: `<div v-for="v of ['a']">{{ v }}</div>`
  }
  const container = document.createElement('div')
  createApp(App).mount(container)
  expect(EMPTY_ARR.length).toBe(0)
})

test('BigInt support', () => {
  const app = createApp({
    template: `<div>{{ BigInt(BigInt(100000111)) + BigInt(2000000000n) * 30000000n }}</div>`
  })
  const root = document.createElement('div')
  app.mount(root)
  expect(root.innerHTML).toBe('<div>60000000100000111</div>')
})

test('isCustomElement', () => {
  const app = createApp({
    template: `<foo/>`
  })
  app.config.compilerOptions.isCustomElement = (tag: string) => tag === 'foo'
  const root = document.createElement('div')
  app.mount(root)
  expect(root.innerHTML).toBe('<foo></foo>')
})

test('comments', () => {
  const app = createApp({
    template: `<div/><!--test--><div/>`
  })
  app.config.compilerOptions.comments = true
  // the comments option is only relevant in production mode
  __DEV__ = false
  const root = document.createElement('div')
  app.mount(root)
  expect(root.innerHTML).toBe('<div></div><!--test--><div></div>')
  __DEV__ = true
})

test('whitespace', () => {
  const app = createApp({
    template: `<div><span/>\n  <span/></div>`
  })
  app.config.compilerOptions.whitespace = 'preserve'
  const root = document.createElement('div')
  app.mount(root)
  expect(root.firstChild!.childNodes.length).toBe(3)
  expect(root.firstChild!.childNodes[1].nodeType).toBe(Node.TEXT_NODE)
})

test('delimiters', () => {
  const app = createApp({
    data: () => ({ foo: 'hi' }),
    template: `[[ foo ]]`
  })
  app.config.compilerOptions.delimiters = [`[[`, `]]`]
  const root = document.createElement('div')
  app.mount(root)
  expect(root.textContent).toBe('hi')
})

