      view: 'view-a'
    },
    components: {
      'view-a': resolve => {
        setTimeout(() => {
          resolve({
            template: '<div>A</div>'
          });
          Vue.nextTick(step1);
        }, 0);
      },
      'view-b': resolve => {
        setTimeout(() => {
          resolve({
            template: '<p>B</p>'
          });
          Vue.nextTick(step2);
        }, 0);
      }
    }
  }).$mount();
  let aCalled = false;

  function step1() {
    // ensure A is resolved only once
    expect(aCalled).toBe(false);
    aCalled = true;
    expect(vm.$el.tagName).toBe('DIV');
    expect(vm.$el.textContent).toBe('A');
    vm.view = 'view-b';
  }

  function step2() {
    expect(vm.$el.tagName).toBe('P');
    expect(vm.$el.textContent).toBe('B');
    vm.view = 'view-a';
    waitForUpdate(function () {
      expect(vm.$el.tagName).toBe('DIV');
      expect(vm.$el.textContent).toBe('A');
    }).then(done);
  }
});

