  }).then(() => {
    // should prune B because max cache reached
    assertCount([1, 1, 1, 1, 1, 0]);
    vm.n = 'bb';
  }).then(() => {
    // B is recreated
    assertCount([1, 1, 2, 1, 1, 1]);
    vm.n = 'aa';
  }).then(() => {
    // B is destroyed and A recreated
    assertCount([2, 1, 2, 2, 1, 1]);
  }).then(done);
});

