      const iterator = {
        index: 0,
        models: this.models,

        next() {
          if (this.index < this.models.length) {
            return {
              value: this.models[this.index++]
            };
          } else {
            return {
              done: true
            };
          }
        }

      };
      return iterator;
    }

  };
  const vm = new Vue({
    template: `
      <div>
        <span v-for="item in list">{{item}}</span>
      </div>
    `,
    data: {
      list: iterable
    }
