it('without key', done => {
  const vm = new Vue({
    data: {
      items: [{
        id: 1,
        msg: 'a'
      }, {
        id: 2,
        msg: 'b'
      }, {
        id: 3,
        msg: 'c'
      }]
    },
    template: '<div><div v-for="item in items">{{ item.msg }}</div></div>'
  }).$mount();
  expect(vm.$el.textContent).toBe('abc');
  const first = vm.$el.children[0];
  vm.items.reverse();
  waitForUpdate(() => {
    expect(vm.$el.textContent).toBe('cba'); // assert reusing DOM element in place

    expect(vm.$el.children[0]).toBe(first);
  }).then(done);
});

it('with key', done => {
  const vm = new Vue({
    data: {
      items: [{
        id: 1,
        msg: 'a'
      }, {
        id: 2,
        msg: 'b'
      }, {
        id: 3,
        msg: 'c'
      }]
    },
    template: '<div><div v-for="item in items" :key="item.id">{{ item.msg }}</div></div>'
  }).$mount();
  expect(vm.$el.textContent).toBe('abc');
  const first = vm.$el.children[0];
  vm.items.reverse();
  waitForUpdate(() => {
