it('template v-for', done => {
  const vm = new Vue({
    data: {
      list: [{
        a: 1
      }, {
        a: 2
      }, {
        a: 3
      }]
    },
    template: '<div>' + '<template v-for="item in list">' + '<p>{{item.a}}</p>' + '<p>{{item.a + 1}}</p>' + '</template>' + '</div>'
  }).$mount();
  assertMarkup();
  vm.list.reverse();
  waitForUpdate(() => {
    assertMarkup();
