    });
  }).then(done);

  function assertMarkup() {
    const markup = vm.list.map(function (item) {
      return '<p>' + item.a + '</p><p>' + (item.a + 1) + '</p>';
    }).join('');
    expect(vm.$el.innerHTML).toBe(markup);
  }
});

