  }).then(() => {
    expect(vm.$el.innerHTML).toBe('123');
    vm.a = 0;
  }).then(() => {
    expect(vm.$el.innerHTML).toBe('0');
    vm.a = ' ';
  }).then(() => {
    expect(vm.$el.innerHTML).toBe(' ');
    vm.a = '    ';
  }).then(() => {
    expect(vm.$el.innerHTML).toBe('    ');
    vm.a = null;
  }).then(() => {
