    template: `
      <div>
        <input type="checkbox" ref="normal" @click="foo"/>
        <input type="checkbox" ref="passive" @click.passive="foo"/>
        <input type="checkbox" ref="exclusive" @click.prevent.passive/>
      </div>
    `,
    methods: {
      foo(e) {
        e.preventDefault();
      }

    }
  });
