it('should call hooks from mixins first', () => {
  const a = {};
  const b = {};
  const c = {};

  const f1 = function () {};

  const f2 = function () {};

  const f3 = function () {};

  const mixinA = {
    a: 1,
    template: 'foo',
    directives: {
      a: a
    },
    created: f1
  };
  const mixinB = {
    b: 1,
    directives: {
      b: b
    },
    created: f2
  };
  const result = mergeOptions({}, {
    directives: {
      c: c
    },
    template: 'bar',
    mixins: [mixinA, mixinB],
    created: f3
  });
  expect(result.a).toBe(1);
