it('events', done => {
  let next;
  const beforeEnterSpy = jasmine.createSpy();
  const afterEnterSpy = jasmine.createSpy();
  const afterLeaveSpy = jasmine.createSpy();
  const vm = new Vue({
    template: `
      <div>
        <transition-group @before-enter="beforeEnter" @after-enter="afterEnter" @after-leave="afterLeave">
          <div v-for="item in items" :key="item" class="test">{{ item }}</div>
        </transition-group>
      </div>
    `,
    data: {
      items: ['a', 'b', 'c']
    },
    methods: {
      beforeEnter(el) {
        expect(el.textContent).toBe('d');
        beforeEnterSpy();
      },

      afterEnter(el) {
        expect(el.textContent).toBe('d');
        afterEnterSpy();
        next();
      },

      afterLeave(el) {
        expect(el.textContent).toBe('a');
        afterLeaveSpy();
        next();
      }

    }
  }).$mount(el);
  vm.items.push('d');
  waitForUpdate(() => {
    expect(vm.$el.innerHTML).toBe(`<span>` + `<div class="test">a</div>` + `<div class="test">b</div>` + `<div class="test">c</div>` + `<div class="test v-enter v-enter-active">d</div>` + `</span>`);
    expect(beforeEnterSpy.calls.count()).toBe(1);
  }).thenWaitFor(_next => {
