it('generate v-for directive', () => {
  assertCodegen('<div><li v-for="item in items" :key="item.uid"></li></div>', `with(this){return _c('div',_l((items),function(item){return _c('li',{key:item.uid})}),0)}`); // iterator syntax

  assertCodegen('<div><li v-for="(item, i) in items"></li></div>', `with(this){return _c('div',_l((items),function(item,i){return _c('li')}),0)}`);
  assertCodegen('<div><li v-for="(item, key, index) in items"></li></div>', `with(this){return _c('div',_l((items),function(item,key,index){return _c('li')}),0)}`); // destructuring

  assertCodegen('<div><li v-for="{ a, b } in items"></li></div>', `with(this){return _c('div',_l((items),function({ a, b }){return _c('li')}),0)}`);
  assertCodegen('<div><li v-for="({ a, b }, key, index) in items"></li></div>', `with(this){return _c('div',_l((items),function({ a, b },key,index){return _c('li')}),0)}`); // v-for with extra element

  assertCodegen('<div><p></p><li v-for="item in items"></li></div>', `with(this){return _c('div',[_c('p'),_l((items),function(item){return _c('li')})],2)}`);
});

