it('create on already observed object', () => {
  // on object
  const obj = {};
  let val = 0;
  let getCount = 0;
  Object.defineProperty(obj, 'a', {
    configurable: true,
    enumerable: true,

    get() {
      getCount++;
      return val;
    },

    set(v) {
      val = v;
    }

  });
  const ob1 = observe(obj);
  expect(ob1 instanceof Observer).toBe(true);
  expect(ob1.value).toBe(obj);
  expect(obj.__ob__).toBe(ob1);
  getCount = 0; // Each read of 'a' should result in only one get underlying get call

  obj.a;
  expect(getCount).toBe(1);
  obj.a;
  expect(getCount).toBe(2); // should return existing ob on already observed objects

  const ob2 = observe(obj);
  expect(ob2).toBe(ob1); // should call underlying setter

  obj.a = 10;
  expect(val).toBe(10);
});

it('create on property with only getter', () => {
