it('should call `update` listener', () => {
  const result1 = [];
  const result2 = [];

  function cb(result, oldVnode, newVnode) {
    if (result.length > 1) {
      expect(result[result.length - 1]).toEqual(oldVnode);
    }

    result.push(newVnode);
  }

  const vnode1 = new VNode('div', {}, [new VNode('span', {}, undefined, 'first sibling'), new VNode('div', {
    hook: {
      update: cb.bind(null, result1)
    }
  }, [new VNode('span', {}, undefined, 'child 1'), new VNode('span', {
    hook: {
      update: cb.bind(null, result2)
    }
  }, undefined, 'child 2')])]);
  const vnode2 = new VNode('div', {}, [new VNode('span', {}, undefined, 'first sibling'), new VNode('div', {
    hook: {
      update: cb.bind(null, result1)
    }
