it('destroyInstance', done => {
  const id = String(Date.now() * Math.random());
  const instance = createInstance(id, `
    new Vue({
      data: {
        x: 'Hello'
      },
      render: function (createElement) {
        return createElement('div', {}, [
          createElement('text', { attrs: { value: this.x }}, [])
        ])
      },
      el: "body"
    })
  `);
  expect(getRoot(instance)).toEqual({
    type: 'div',
    children: [{
      type: 'text',
      attr: {
        value: 'Hello'
      }
    }]
  });
  instance.$destroy();
  setTimeout(() => {
    expect(instance.document).toBeUndefined();
    expect(instance.app).toBeUndefined();
    done();
  }, 0);
});

it('refreshInstance', done => {
  const id = String(Date.now() * Math.random());
  const instance = createInstance(id, `
    new Vue({
      data: {
        x: 'Hello'
      },
      render: function (createElement) {
        return createElement('div', {}, [
          createElement('text', { attrs: { value: this.x }}, [])
        ])
      },
      el: "body"
    })
  `);
  expect(getRoot(instance)).toEqual({
    type: 'div',
    children: [{
      type: 'text',
      attr: {
        value: 'Hello'
      }
    }]
  });
  instance.$refresh({
    x: 'World'
  });
  setTimeout(() => {
    expect(getRoot(instance)).toEqual({
      type: 'div',
      children: [{
        type: 'text',
        attr: {
          value: 'World'
        }
      }]
    });
    instance.$destroy();
    const result = instance.$refresh({
      x: 'World'
    });
    expect(result instanceof Error).toBe(true);
    done();
  });
});

