it('using global mixins', () => {
  const id = String(Date.now() * Math.random());
  const instance = createInstance(id, `
    Vue.mixin({
      created () {
        this.test = true
      }
    })
    const Test = {
      data: () => ({ test: false }),
      render (h) {
        return h('div', this.test ? 'Hello' : 'nope')
      }
    }
    new Vue({
      data: { test: false },
      render (h) {
        return this.test ? h(Test) : h('p')
      },
      el: 'body'
    })
  `);
  expect(getRoot(instance)).toEqual({
    type: 'div',
    children: [{
      type: 'text',
      attr: {
        value: 'Hello'
      }
    }]
  });
});

