it('should be generated with module diff', done => {
  const id = String(Date.now() * Math.random());
  const instance = createInstance(id, `
    new Vue({
      data: {
        counter: 0
      },
      methods: {
        foo: function () {}
      },
      render: function (createElement) {
        switch (this.counter) {
          case 1:
          return createElement('div', {}, [
            createElement('text', { attrs: { value: 'World' }}, [])
          ])

          case 2:
          return createElement('div', {}, [
            createElement('text', { attrs: { value: 'World' }, style: { fontSize: 100 }}, [])
          ])

          case 3:
          return createElement('div', {}, [
            createElement('text', {
              attrs: { value: 'World' },
              style: { fontSize: 100 },
              on: { click: this.foo }
            }, [])
          ])

          case 4:
          return createElement('div', {}, [
            createElement('text', {
              attrs: { value: 'Weex' },
              style: { color: '#ff0000' }
            }, [])
          ])

          default:
          return createElement('div', {}, [
            createElement('text', { attrs: { value: 'Hello' }}, [])
          ])
        }
      },
      el: "body"
    })
  `);
  expect(getRoot(instance)).toEqual({
    type: 'div',
    children: [{
      type: 'text',
      attr: {
        value: 'Hello'
      }
    }]
  });
  syncPromise([checkRefresh(instance, {
    counter: 1
  }, result => {
    expect(result).toEqual({
      type: 'div',
      children: [{
        type: 'text',
        attr: {
          value: 'World'
        }
      }]
    });
  }), checkRefresh(instance, {
    counter: 2
  }, result => {
    expect(result).toEqual({
      type: 'div',
      children: [{
        type: 'text',
        attr: {
          value: 'World'
        },
        style: {
          fontSize: 100
        }
      }]
    });
  }), checkRefresh(instance, {
    counter: 3
  }, result => {
    expect(result).toEqual({
      type: 'div',
      children: [{
        type: 'text',
        attr: {
          value: 'World'
        },
        style: {
          fontSize: 100
        },
        event: ['click']
      }]
    });
  }), checkRefresh(instance, {
    counter: 4
  }, result => {
    expect(result).toEqual({
      type: 'div',
      children: [{
        type: 'text',
        attr: {
          value: 'Weex'
        },
        style: {
          fontSize: '',
          color: '#ff0000'
        }
      }]
    });
    done();
  })]);
});

