it('should be generated with if/for diff', done => {
  const {
    render,
    staticRenderFns
  } = compileAndStringify(`
    <div>
      <text v-for="item in list" v-if="item.x">{{item.v}}</text>
    </div>
  `);
  const id = String(Date.now() * Math.random());
  const instance = createInstance(id, `
    new Vue({
      data: {
        list: [
          { v: 'Hello', x: true },
          { v: 'World', x: false },
          { v: 'Weex', x: true }
        ]
      },
      computed: {
        x: {
          get: function () { return 0 },
          set: function (v) {
            switch (v) {
              case 1:
              this.list[1].x = true
              break
              case 2:
              this.list.push({ v: 'v-if' })
              break
              case 3:
              this.list.push({ v: 'v-for', x: true })
              break
              case 4:
              this.list.splice(1, 2)
              break
            }
          }
        }
      },
      render: ${render},
      staticRenderFns: ${staticRenderFns},
      el: "body"
    })
  `);
  expect(getRoot(instance)).toEqual({
    type: 'div',
    children: [{
      type: 'text',
      attr: {
        value: 'Hello'
      }
    }, {
      type: 'text',
      attr: {
        value: 'Weex'
      }
    }]
  });
  syncPromise([checkRefresh(instance, {
    x: 1
  }, result => {
    expect(result).toEqual({
      type: 'div',
      children: [{
        type: 'text',
        attr: {
          value: 'Hello'
        }
      }, {
        type: 'text',
        attr: {
          value: 'World'
        }
      }, {
        type: 'text',
        attr: {
          value: 'Weex'
        }
      }]
    });
  }), checkRefresh(instance, {
    x: 2
  }, result => {
    expect(result).toEqual({
      type: 'div',
      children: [{
        type: 'text',
        attr: {
          value: 'Hello'
        }
      }, {
        type: 'text',
        attr: {
          value: 'World'
        }
      }, {
        type: 'text',
        attr: {
          value: 'Weex'
        }
      }]
    });
  }), checkRefresh(instance, {
    x: 3
  }, result => {
    expect(result).toEqual({
      type: 'div',
      children: [{
        type: 'text',
        attr: {
          value: 'Hello'
        }
      }, {
        type: 'text',
        attr: {
          value: 'World'
        }
      }, {
        type: 'text',
        attr: {
          value: 'Weex'
        }
      }, {
        type: 'text',
        attr: {
          value: 'v-for'
        }
      }]
    });
  }), checkRefresh(instance, {
    x: 4
  }, result => {
    expect(result).toEqual({
      type: 'div',
      children: [{
        type: 'text',
        attr: {
          value: 'Hello'
        }
      }, {
        type: 'text',
        attr: {
          value: 'v-for'
        }
      }]
    });
    done();
  })]);
});

