    expect(result).toEqual({
      type: 'div',
      children: [{
        type: 'text',
        attr: {
          value: '1-2'
        }
      }]
    });
  }), checkRefresh(instance, {
    counter: 3
  }, result => {
    expect(result).toEqual({
      type: 'div',
      children: [{
        type: 'text',
        attr: {
          value: 'Bar'
        },
        style: {
          fontSize: 100
        }
      }]
    });
  }), checkRefresh(instance, {
    counter: 4
  }, result => {
    expect(result).toEqual({
      type: 'div',
      children: [{
        type: 'image',
        attr: {
          src: 'http://example.com/favicon.ico'
        }
      }]
    });
