it('should be generated by array binding', done => {
  compileAndExecute(`
    <div>
      <text :style="[x, y]" @click="foo">Hello {{z}}</text>
    </div>
  `, `
    data: {
      x: { fontSize: 100, color: '#00ff00' },
      y: { color: '#ff0000', fontWeight: 'bold' },
      z: 'World'
    },
    methods: {
      foo: function () {
        this.x.fontSize = 200
        this.x.color = '#0000ff'
        Vue.delete(this.y, 'fontWeight')
        this.z = 'Weex'
      }
    }
  `).then(instance => {
    expect(getRoot(instance)).toEqual({
      type: 'div',
      children: [{
        type: 'text',
        event: ['click'],
        style: {
          fontSize: 100,
          color: '#ff0000',
          fontWeight: 'bold'
        },
        attr: {
          value: 'Hello World'
        }
      }]
    });
    fireEvent(instance, instance.document.body.children[0].ref, 'click');
    return instance;
  }).then(instance => {
    expect(getRoot(instance)).toEqual({
      type: 'div',
      children: [{
        type: 'text',
        event: ['click'],
        style: {
          fontSize: 200,
          color: '#ff0000',
          fontWeight: ''
        },
        attr: {
          value: 'Hello Weex'
        }
      }]
    });
    done();
  });
});

