it("should bubble up errors when wrapped in a promise and bail is true", async () => {
  try {
    const createCompiler = options => {
      return new Promise((resolve, reject) => {
        const webpack = require("..");
        const c = webpack(options);
        c.run((err, stats) => {
          if (err) {
            reject(err);
          }
          if (stats !== undefined && "errors" in stats) {
            reject(err);
          } else {
            resolve(stats);
          }
        });
        return c;
      });
    };
    compiler = await createCompiler({
      context: __dirname,
      mode: "production",
      entry: "./missing-file",
      output: {
        path: "/directory",
        filename: "bundle.js"
      },
      bail: true
    });
  } catch (err) {
    expect(err.toString()).toMatch("ModuleNotFoundError: Module not found: Error: Can't resolve './missing-file'");
  }
});

