      a: 5,
      b: 5
    })).toMatchInlineSnapshot(`
		Array [
		  Object {
		    "items": Array [
		      0,
		      1,
		    ],
		    "size": Object {
		      "a": 3,
		      "b": 4,
		    },
		  },
		  Object {
		    "items": Array [
		      2,
		    ],
		    "size": Object {
		      "a": 3,
		    },
		  },
		]
	`);
  });

  it("should separate items with different size types when unsplittable", () => {
    expect(group([{
      a: 1
    }, {
      b: 1
    }, {
      a: 1
    }, {
      a: 1
    }, {
      b: 1
    }, {
      a: 1
    }, {
      a: 1
    }, {
      b: 1
    }, {
      a: 1
    }, {
      a: 1
    }], {
      a: 3,
      b: 3
    }, {
      a: 5,
      b: 5
    })).toMatchInlineSnapshot(`
		Array [
		  Object {
		    "items": Array [
		      0,
		      2,
		      3,
		    ],
		    "size": Object {
		      "a": 3,
		    },
		  },
		  Object {
		    "items": Array [
