it("should build the bundle correctly", done => {
  const compiler = webpack({
    mode: "development",
    entry: loaderPath + "!" + filePath,
    output: {
      path: "/directory",
      filename: "bundle.js"
    }
  });
  const memfs = compiler.outputFileSystem = createFsFromVolume(new Volume());
  let onChange;
  compiler.hooks.done.tap("WatchDetectionTest", () => {
    if (onChange) onChange();
  });
  let watcher;
  step1();
  function step1() {
    onChange = () => {
      if (memfs.readFileSync("/directory/bundle.js") && memfs.readFileSync("/directory/bundle.js").toString().indexOf("original") >= 0) step2();
    };
    watcher = compiler.watch({
      aggregateTimeout: 50
    }, () => {});
  }
  function step2() {
    onChange = () => {
      expect(compiler.modifiedFiles).not.toBe(undefined);
      expect(compiler.removedFiles).not.toBe(undefined);
    };
    fs.writeFile(filePath, "require('./file2'); again", "utf-8", handleError);
    setTimeout(step3, changeTimeout);
  }
  function step3() {
    if (invalidate) watcher.invalidate();
    fs.writeFile(file2Path, "wrong", "utf-8", handleError);
    setTimeout(step4, changeTimeout);
  }
  function step4() {
    onChange = () => {
      expect(compiler.modifiedFiles).not.toBe(undefined);
      expect(compiler.removedFiles).not.toBe(undefined);
      if (memfs.readFileSync("/directory/bundle.js").toString().indexOf("correct") >= 0) step5();
    };
    fs.writeFile(file2Path, "correct", "utf-8", handleError);
  }
  function step5() {
    onChange = null;
    watcher.close(() => {
      setTimeout(done, 500);
    });
  }
  function handleError(err) {
    if (err) done(err);
  }
});

