});

it('handles backpressure (1/3)', done => {
  const wss = new WebSocket.Server({
    port: 0
  }, () => {
    // eslint-disable-next-line no-unused-vars
    const ws = new WebSocket(`ws://localhost:${wss.address().port}`);
  });
  wss.on('connection', ws => {
    const duplex = createWebSocketStream(ws);
    duplex.resume();
    duplex.on('drain', () => {
      duplex.on('close', () => {
        wss.close(done);
      });
      duplex.end();
    });
    const chunk = randomBytes(1024);
    let ret;
    do {
      ret = duplex.write(chunk);
    } while (ret !== false);
  });
});

it('handles backpressure (2/3)', done => {
  const wss = new WebSocket.Server({
    port: 0,
    perMessageDeflate: true
  }, () => {
    const called = [];
    const ws = new WebSocket(`ws://localhost:${wss.address().port}`);
    const duplex = createWebSocketStream(ws);
    const read = duplex._read;
    duplex._read = () => {
      duplex._read = read;
      called.push('read');
      assert.ok(ws._receiver._writableState.needDrain);
      read();
      assert.ok(ws._socket.isPaused());
    };
    ws.on('open', () => {
      ws._socket.on('pause', () => {
        duplex.resume();
      });
      ws._receiver.on('drain', () => {
        called.push('drain');
        assert.ok(!ws._socket.isPaused());
        duplex.end();
      });
      const opts = {
        fin: true,
        opcode: 0x02,
        mask: false,
        readOnly: false
      };
      const list = [...Sender.frame(randomBytes(16 * 1024), {
        rsv1: false,
        ...opts
      }), ...Sender.frame(Buffer.alloc(1), {
        rsv1: true,
        ...opts
      })];

      // This hack is used because there is no guarantee that more than
      // 16 KiB will be sent as a single TCP packet.
      ws._socket.push(Buffer.concat(list));
    });
    duplex.on('close', () => {
      assert.deepStrictEqual(called, ['read', 'drain']);
      wss.close(done);
    });
  });
});

it('handles backpressure (3/3)', done => {
  const wss = new WebSocket.Server({
    port: 0,
    perMessageDeflate: true
  }, () => {
    const called = [];
    const ws = new WebSocket(`ws://localhost:${wss.address().port}`);
    const duplex = createWebSocketStream(ws);
    const read = duplex._read;
    duplex._read = () => {
      called.push('read');
      assert.ok(!ws._receiver._writableState.needDrain);
      read();
      assert.ok(!ws._socket.isPaused());
      duplex.end();
    };
    ws.on('open', () => {
      ws._receiver.on('drain', () => {
        called.push('drain');
        assert.ok(ws._socket.isPaused());
        duplex.resume();
      });
      const opts = {
        fin: true,
        opcode: 0x02,
        mask: false,
        readOnly: false
      };
      const list = [...Sender.frame(randomBytes(16 * 1024), {
        rsv1: false,
        ...opts
      }), ...Sender.frame(Buffer.alloc(1), {
        rsv1: true,
        ...opts
      })];
      ws._socket.push(Buffer.concat(list));
    });
    duplex.on('close', () => {
      assert.deepStrictEqual(called, ['drain', 'read']);
      wss.close(done);
    });
  });
});

