it('parses quoted params', () => {
  assert.deepStrictEqual(parse('foo;bar="hi"'), {
    foo: [{
      bar: ['hi'],
      __proto__: null
    }],
    __proto__: null
  });
  assert.deepStrictEqual(parse('foo;bar="\\0"'), {
    foo: [{
      bar: ['0'],
      __proto__: null
    }],
    __proto__: null
  });
  assert.deepStrictEqual(parse('foo;bar="b\\a\\z"'), {
    foo: [{
      bar: ['baz'],
      __proto__: null
    }],
    __proto__: null
  });
  assert.deepStrictEqual(parse('foo;bar="b\\az";bar'), {
    foo: [{
      bar: ['baz', true],
      __proto__: null
    }],
    __proto__: null
  });
  assert.throws(() => parse('foo;bar="baz"qux'), /^SyntaxError: Unexpected character at index 13$/);
  assert.throws(() => parse('foo;bar="baz" qux'), /^SyntaxError: Unexpected character at index 14$/);
});

