  }));
  let gotPing = false;
  receiver.on('message', (data, isBinary) => {
    assert.deepStrictEqual(data, msg);
    assert.ok(!isBinary);
    assert.ok(gotPing);
    done();
  });
  receiver.on('ping', data => {
