        req: {
          socket
        }
      }, cb) => {
        assert.strictEqual(socket.readable, true);
        assert.strictEqual(socket.writable, true);
        socket.on('end', () => {
          assert.strictEqual(socket.readable, false);
          assert.strictEqual(socket.writable, true);
          cb(true);
        });
      },
      server
    });
