      done(new Error("Unexpected 'connection' event"));
    });
    const socket = net.connect({
      port: server.address().port,
      allowHalfOpen: true
    }, () => {
      socket.end(['GET / HTTP/1.1', 'Host: localhost', 'Upgrade: websocket', 'Connection: Upgrade', 'Sec-WebSocket-Key: dGhlIHNhbXBsZSBub25jZQ==', 'Sec-WebSocket-Version: 13', '\r\n'].join('\r\n'));
