    ws.on('message', (message, isBinary) => {
      assert.ok(!isBinary);
      messages.push(message.toString());
    });
    ws.on('close', (code, reason) => {
      assert.deepStrictEqual(messages, ['foo', 'bar', 'baz', 'qux']);
